$pop = new Zend_Mail_Protocol_Pop3();\n$pop->connect('your_pop3_server', 110);\n$pop->login('your_username', 'your_password');\n\n// Authenticate with POP3 server\nif ($pop->hasMail()) {\n    // Get the first email to trigger authentication\n    $pop->retr(1);\n}\n\n// Now, use SMTP to send your email\n$smtp = new Zend_Mail_Transport_Smtp('your_smtp_server', array(\n    'auth' => 'login',\n    'username' => 'your_username',\n    'password' => 'your_password',\n    'port' => 25,\n));\n\n$mail = new Zend_Mail();\n$mail->setFrom('your_from_email', 'Your Name');\n$mail->addTo('recipient_email', 'Recipient Name');\n$mail->setSubject('Test Email');\n$mail->setBodyText('This is a test email');\n\n$mail->send($smtp);\n\n// Don't forget to close the POP3 connection\n$pop->quit();