function shorten_url($params) {\n  $secret_key = 'your_secret_key_here';\n  $query_string = http_build_query($params);\n  $hash = hash_hmac('sha256', $query_string, $secret_key);\n  return 'http://example.com/script.php?p=' . substr($hash, 0, 8); // shorten to 8 chars\n}\n\n$params = array(\n  'param1' => 'value1',\n  'param2' => 'value2',\n  'param3' => 'value3',\n  'param4' => 'value4',\n  'param5' => 'value5'\n);\n\n$short_url = shorten_url($params);\necho $short_url;