function extracting_comments($table, $fields, $condition, $order, $limit) {\n    $query = "SELECT " . $fields .\n        " FROM " . $table .\n        " WHERE " . $condition .\n        " ORDER BY " . $order .\n        " LIMIT " . $limit . " ";\n\n    if ($stmt = $this->conn->prepare($query)) {\n        $stmt->execute();\n        $row = array_pad(array(), $stmt->field_count, '');\n        $params = array();\n        foreach ($row as $k => $v) {\n            $params[] = &$row[$k];\n        }\n        call_user_func_array(array($stmt, 'bind_result'), $params);\n        $i = 0;\n        $result = array(); // Initialize an empty array\n        while ($stmt->fetch()) {\n            $i++;\n            $name = 't' . $i;\n            $$name = array();\n            foreach ($row as $b => $elem) {\n                $atul[$b] = $row[$b];\n            }\n            $$name = $atul;\n            $result[] = $$name; // Add the element to the array\n        }\n        $stmt->close();\n        return $result; // Return the dynamic array\n    }\n}