// Assuming you're using MySQLi, as mysql_ functions are deprecated\n$mysqli = new mysqli('your_host', 'your_user', 'your_password', 'your_database');\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepared statement with SQL join\n$stmt = $mysqli->prepare("SELECT n.*, u.username FROM news n JOIN users u ON n.user_id = u.id WHERE n.id = ?");\n\n// Bind parameter\n$stmt->bind_param("i", $_GET['id']);\n\n// Execute query\n$stmt->execute();\n\n// Get result\n$result = $stmt->get_result();\n\n// Fetch data\n$news = $result->fetch_assoc();\n\n// Close statement and connection\n$stmt->close();\n$mysqli->close();