// PluginLoader.php\n\nuse Composer\Autoloader\ClassLoader;\n\nclass PluginLoader\n{\n    private $loader;\n\n    public function __construct(ClassLoader $loader)\n    {\n        $this->loader = $loader;\n    }\n\n    public function loadPlugins()\n    {\n        $plugins = [];\n        foreach (glob(__DIR__ . '/plugins/*.php') as $file) {\n            $pluginClass = basename($file, '.php');\n            $this->loader->loadClass($pluginClass);\n            $plugins[] = new $pluginClass();\n        }\n        return $plugins;\n    }\n}