function copyright_date($creation_year = null) {\n    $current_year = date('Y');\n\n    if (is_null($creation_year)) {\n        // handle the case when the argument is not provided\n        echo "Error: Creation year is required";\n    } elseif ($creation_year == $current_year) {\n        echo $current_year;\n    } else {\n        echo $creation_year . '-' . $current_year;\n    }\n}