function formatDuration($time) {\n    list($hours, $minutes, $seconds) = explode(':', $time);\n    \n    $output = [];\n    if ($hours > 0) {\n        $output[] = $hours . ' hour' . ($hours > 1 ? 's' : '');\n    }\n    if ($minutes > 0 || count($output)) {\n        $output[] = $minutes . ' minute' . ($minutes > 1 ? 's' : '');\n    }\n    $output[] = $seconds . ' second' . ($seconds > 1 ? 's' : '');\n    \n    return implode(', ', $output);\n}\n\n// Example usage:\necho formatDuration('01:02:03') . "\n"; // Outputs: 1 hour, 2 minutes, 3 seconds\necho formatDuration('00:02:03') . "\n"; // Outputs: 2 minutes, 3 seconds\necho formatDuration('00:00:03') . "\n"; // Outputs: 3 seconds