// Assuming you have the data in a MySQL result object $result\n\n// Set header for CSV file download\nheader('Content-Type: text/csv');\nheader('Content-Disposition: attachment; filename="user_data.csv"');\n\n// Open output buffer\nob_start();\n\n// Create CSV file\n$fp = fopen('php://output', 'w');\nfputcsv($fp, array('Column 1', 'Column 2', 'Column 3')); // header row\n\n// Loop through MySQL result and add rows to CSV\nwhile ($row = mysql_fetch_assoc($result)) {\n    fputcsv($fp, $row);\n}\n\n// Close output buffer and flush to browser\nfclose($fp);\nob_end_flush();