$chargeCode = 'AJ';\n$agentTypes = ['A' => 'AgentType1', 'J' => 'AgentType2', 'L' => 'AgentType3', 'A2' => 'AgentType2'];\n\n$pattern = '/(A|J|L|A2)/';\npreg_match_all($pattern, $chargeCode, $matches);\n\n$divide = count($matches[0]);\n$agents = array_unique($matches[0]);\n\n$amount = 50;\n$result = $amount / $divide;\n\nforeach ($agents as $agent) {\n    echo "Agent {$agentTypes[$agent]} gets $result\n";\n}