$uploadDir = '/path/to/uploads/';\n$fileName = basename($_FILES['file']['name']);\n$fileType = $_FILES['file']['type'];\n$fileSize = $_FILES['file']['size'];\n\n// Validate file type and size\nif ($fileType !== 'audio/mpeg' || $fileSize > 10000000) {\n  // handle invalid file type or size\n}\n\n// Rename file to prevent guessing\n$randomName = uniqid() . '.' . pathinfo($fileName, PATHINFO_EXTENSION);\n$file_path = $uploadDir . $randomName;\n\n// Move uploaded file to secure location\nmove_uploaded_file($_FILES['file']['tmp_name'], $file_path);\n\n// Store file metadata in database\n$mysqli = new mysqli('your_host', 'your_user', 'your_password', 'your_database');\n$stmt = $mysqli->prepare('INSERT INTO files (filename, file_type, file_size, upload_date, file_path) VALUES (?, ?, ?, NOW(), ?)');\n$stmt->bind_param('ssis', $fileName, $fileType, $fileSize, $file_path);\n$stmt->execute();