function packRatings(array $ratings): int {\n    $packed = 0;\n    foreach ($ratings as $rating) {\n        $packed = ($packed << 3) | ($rating - 1); // Subtract 1 to fit in 3 bits\n    }\n    return $packed;\n}\n\nfunction unpackRatings(int $packed): array {\n    $ratings = [];\n    for ($i = 0; $i < 5; $i++) {\n        $ratings[] = (($packed >> (3 * (4 - $i))) & 7) + 1; // Add 1 to restore original value\n    }\n    return $ratings;\n}\n\n// Example usage:\n$ratings = [3, 5, 2, 1, 4];\n$packed = packRatings($ratings);\necho "Packed: $packed\n";\n\n$unpacked = unpackRatings($packed);\necho "Unpacked: " . implode(", ", $unpacked) . "\n";