use Symfony\Component\HttpFoundation\Session\Session;\nuse Symfony\Component\HttpFoundation\Session\Storage\NativeSessionStorage;\nuse Symfony\Component\HttpFoundation\Session\Storage\Handler\PdoSessionHandler;\n\n// Create a new session\n$session = new Session(new NativeSessionStorage(), new PdoSessionHandler('mysql:host=localhost;dbname=sessions', 'username', 'password'));\n\n// Start the session\n$session->start();\n\n// Set some data\n$session->set('foo', 'bar');\n\n// Get some data\n$foo = $session->get('foo');\n\n// Flash data\n$session->getFlashBag()->add('flash', 'This is a flash message');\n\n// Regenerate the session ID\n$session->migrate();\n\n// Save the session\n$session->save();