// Initialize curl\n$ch = curl_init();\n\n// Set the URL and options for the login request\ncurl_setopt($ch, CURLOPT_URL, 'https://example.com/login');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, 'username=your_username&password=your_password');\ncurl_setopt($ch, CURLOPT_COOKIEJAR, 'cookies.txt'); // Save cookies to a file\n\n// Execute the login request\ncurl_exec($ch);\n\n// Set the URL and options for the request to the protected page\ncurl_setopt($ch, CURLOPT_URL, 'https://example.com/protected_page');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_COOKIEFILE, 'cookies.txt'); // Load cookies from the file\n\n// Execute the request to the protected page\n$response = curl_exec($ch);\n\n// Print the content of the protected page\necho $response;\n\n// Close the curl session\ncurl_close($ch);