// StranglerController.php\nclass StranglerController extends Zend_Controller_Action\n{\n    public function indexAction()\n    {\n        $request = $this->getRequest();\n        $url = 'http://old-application.com' . $request->getPathInfo();\n        $ch = curl_init($url);\n        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n        $response = curl_exec($ch);\n        curl_close($ch);\n        $this->getResponse()->setBody($response);\n    }\n}