$objMemcache = new Memcache();\n$objMemcache->connect('127.0.0.1', 11211);\n\n$obj = new User();\n$obj->setfname('John');\n$obj->setage(32);\n\n// Serialize the object before storing it in Memcache\n$objSerialized = serialize($obj);\n$objMemcache->set('user1', $objSerialized, false, 60);\n\n// To retrieve the object, you need to unserialize it\n$objRetrieved = unserialize($objMemcache->get('user1'));\nvar_dump($objRetrieved);