$Q = "SELECT \n        n.id AS news_id, \n        n.item AS news_item, \n        a.id AS author_id, \n        a.name AS author_name\n      FROM \n        `news` n\n      JOIN \n        `authors` a ON n.author_id = a.id";\n\n$query = $this->lite->query($Q) or die($this->lite->error); \n\n$result=null; \nwhile($obj = $query->fetch_object()){\n    $result.= '<li>'.$obj->news_item. ' by ' . $obj->author_name . '</li>';\n}