<?php\nuse PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\Exception;\n\nrequire 'vendor/autoload.php';\n\n$mail = new PHPMailer(true);\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $email = filter_var($_POST['email'], FILTER_VALIDATE_EMAIL);\n    $message = trim($_POST['message']);\n\n    if ($email && $message) {\n        try {\n            $mail->isSMTP();\n            $mail->Host = 'your_smtp_host'; // Replace with your SMTP host\n            $mail->Port = 587; // Replace with your SMTP port\n            $mail->SMTPAuth = true;\n            $mail->Username = 'your_smtp_username'; // Replace with your SMTP username\n            $mail->Password = 'your_smtp_password'; // Replace with your SMTP password\n            $mail->SMTPSecure = 'tls';\n            $mail->setFrom($email, 'Your Name');\n            $mail->addAddress('your_email@example.com', 'Your Name'); // Replace with your email address\n            $mail->Subject = 'Contact Form Submission';\n            $mail->Body = $message;\n\n            $mail->send();\n            echo 'Message sent successfully!';\n        } catch (Exception $e) {\n            echo 'Error sending email: ' . $mail->ErrorInfo;\n        }\n    } else {\n        echo 'Invalid email or message';\n    }\n} else {\n    echo 'Invalid request method';\n}