// controllers/Blog.php\nclass Blog extends CI_Controller {\n    public function index() {\n        $data['entries'] = $this->Blog_model->get_entries();\n        $this->load->view('blog', $data);\n    }\n\n    public function create() {\n        $data = array('title' => $this->input->post('title'), 'content' => $this->input->post('content'));\n        $this->Blog_model->create_entry($data);\n        redirect('blog');\n    }\n\n    public function update($id) {\n        $data = array('title' => $this->input->post('title'), 'content' => $this->input->post('content'));\n        $this->Blog_model->update_entry($id, $data);\n        redirect('blog');\n    }\n\n    public function delete($id) {\n        $this->Blog_model->delete_entry($id);\n        redirect('blog');\n    }\n}