<?php\n// Assuming you want to set a cookie named "my_cookie" with value "hello_world"\n$cookie_name = 'my_cookie';\n$cookie_value = 'hello_world';\n\n// Craft the Set-Cookie header manually\n$header = 'Set-Cookie: ' . $cookie_name . '=' . $cookie_value . ';';\n\n// Add the header to the HTTP response\nheader($header, false);\n\n// Output the rest of your response as usual\necho 'Hello, world!';\n?>