class Page {\n    private $title;\n    private $content;\n    private $header;\n    private $footer;\n\n    public function __construct($title) {\n        $this->title = $title;\n    }\n\n    public function addHeader($header) {\n        $this->header = $header;\n    }\n\n    public function addContent($content) {\n        $this->content = $content;\n    }\n\n    public function addFooter($footer) {\n        $this->footer = $footer;\n    }\n\n    public function render() {\n        echo "<!DOCTYPE html>";\n        echo "<html>";\n        echo "<head>";\n        echo "<title>" . $this->title . "</title>";\n        echo "</head>";\n        echo "<body>";\n        echo $this->header;\n        echo $this->content;\n        echo $this->footer;\n        echo "</body>";\n        echo "</html>";\n    }\n}