$pdo = new PDO("mysql:host=localhost;dbname=database", "username", "password");\n\n$sql = "SELECT * FROM t1;";\n$stmt1 = $pdo->prepare($sql);\n$stmt1->execute();\n\n$sql = "SELECT * FROM t2;";\n$stmt2 = $pdo->prepare($sql);\n$stmt2->execute();\n\n$results = array($stmt1->fetchAll(PDO::FETCH_ASSOC), $stmt2->fetchAll(PDO::FETCH_ASSOC));\n\nprint_r($results[0]); // dump results for t1\nprint_r($results[1]); // dump results for t2