<?php\n// Cache configuration\n$cacheDir = 'cache/';\n$cacheTime = 3600; // 1 hour\n\n// Check if cache exists and is not expired\nif (file_exists($cacheDir . 'index.html') && filemtime($cacheDir . 'index.html') > time() - $cacheTime) {\n    // Serve cached content\n    readfile($cacheDir . 'index.html');\n    exit;\n}\n\n// Start output buffering\nob_start();\n\n// Your PHP code here...\n\n// Get the output buffer contents\n$contents = ob_get_contents();\n\n// Save the contents to cache\nfile_put_contents($cacheDir . 'index.html', $contents);\n\n// Send the output to the browser\nob_end_flush();\n?>