require_once 'getid3/getid3.php';\n\n$mp3_file = 'path/to/your/mp3/file.mp3';\n$album_art = 'path/to/your/album/art/image.jpg';\n\n$getID3 = new getID3;\n$getID3->analyze($mp3_file);\n\n$tag = $getID3->info['tags']['id3v2'];\nif (isset($tag['APIC'])) {\n    // Album art already exists, update it\n    $tag['APIC'][0]['data'] = file_get_contents($album_art);\n    $tag['APIC'][0]['mime'] = 'image/jpeg';\n} else {\n    // Add new album art\n    $tag['APIC'] = array(\n        array(\n            'data' => file_get_contents($album_art),\n            'mime' => 'image/jpeg',\n            'pictureType' => 3, // 3 = Front cover\n        )\n    );\n}\n\n$getID3->info['tags']['id3v2'] = $tag;\n$getID3->writeTags();\n\necho 'Album art updated!';