function hex_dump($string) {\n    $dump = "";\n    for ($i = 0; $i < strlen($string); $i += 16) {\n        $chunk = substr($string, $i, 16);\n        $dump .= sprintf("%08x  ", $i);\n        for ($j = 0; $j < 16; $j++) {\n            if ($j < strlen($chunk)) {\n                $dump .= sprintf("%02x ", ord($chunk[$j]));\n            } else {\n                $dump .= "   ";\n            }\n        }\n        $dump .= " |" . preg_replace('/[^\x20-\x7e]/', '.', $chunk) . "|\n";\n    }\n    return $dump;\n}\n\n$string = "Hello, World!";\necho hex_dump($string);