// Benchmarking\nfunction benchmark($method) {\n    $color = 'red';\n    $start = microtime(true);\n    for ($i = 0; $i < 100000; $i++) {\n        if ($method === 'multiple_args') {\n            echo 'The ball is ', $color;\n        } elseif ($method === 'concat') {\n            echo 'The ball is ' . $color;\n        } elseif ($method === 'double_quotes') {\n            echo "The ball is $color";\n        }\n    }\n    $end = microtime(true);\n    return $end - $start;\n}\n\n// Run the benchmark\n$multipleArgsTime = benchmark('multiple_args');\n$concatTime = benchmark('concat');\n$doubleQuotesTime = benchmark('double_quotes');\n\n// Print the results\necho "Multiple args: $multipleArgsTime seconds\n";\necho "Concat: $concatTime seconds\n";\necho "Double quotes: $doubleQuotesTime seconds\n";