$name = 'introduction';\n$mysqli = new mysqli('localhost', 'user', 'pass', 'db') or die('There was a problem connecting to the database.');\n$stmt = $mysqli->prepare("SELECT name, content FROM sections WHERE name = ?");\n$stmt->bind_param('s', $name);\n$stmt->execute();\n$result = $stmt->get_result(); // Get the result set as an object\n$row = $result->fetch_object(); // Fetch the first row as an object\necho $row->content; // Access the content column\n$stmt->close();