<?php\nfunction encrypt_ssn($ssn) {\n  $key = 'your_secret_encryption_key_here'; // 256-bit key\n  $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-gcm'));\n  $encrypted = openssl_encrypt($ssn, 'aes-256-gcm', $key, 0, $iv, $tag);\n  return base64_encode($iv . $encrypted . $tag);\n}\n\nfunction decrypt_ssn($encrypted_ssn) {\n  $key = 'your_secret_encryption_key_here'; // 256-bit key\n  $encrypted_ssn = base64_decode($encrypted_ssn);\n  $iv = substr($encrypted_ssn, 0, openssl_cipher_iv_length('aes-256-gcm'));\n  $encrypted = substr($encrypted_ssn, openssl_cipher_iv_length('aes-256-gcm'), -16);\n  $tag = substr($encrypted_ssn, -16);\n  return openssl_decrypt($encrypted, 'aes-256-gcm', $key, 0, $iv, $tag);\n}\n\n// Example usage\n$ssn = '123-45-6789';\n$encrypted_ssn = encrypt_ssn($ssn);\necho "Encrypted SSN: $encrypted_ssn\n";\n$decrypted_ssn = decrypt_ssn($encrypted_ssn);\necho "Decrypted SSN: $decrypted_ssn\n";