private $conditions = [];\nprivate $joins = [];\n\npublic function addCondition(Condition $condition, $join = null)\n{\n    $this->conditions[] = $condition;\n    if ($join) {\n        $this->joins[] = $join;\n    }\n}\n\npublic function validate()\n{\n    $result = true;\n    foreach ($this->conditions as $index => $condition) {\n        $conditionResult = $condition->validate();\n        \n        // Apply join logic\n        if (isset($this->joins[$index])) {\n            $join = $this->joins[$index];\n            if ($join == 'AND') {\n                $result = $result && $conditionResult;\n            } elseif ($join == 'OR') {\n                $result = $result || $conditionResult;\n            } elseif ($join == 'NOT') {\n                $result = $result && !$conditionResult;\n            }\n        } else {\n            $result = $conditionResult;\n        }\n    }\n    return $result;\n}