function calculateNextCronExecution($cronExpression, $timestamp) {\n    $cronParts = explode(' ', $cronExpression);\n    $minute = $cronParts[0];\n    $hour = $cronParts[1];\n    $day = $cronParts[2];\n    $month = $cronParts[3];\n    $dayOfWeek = $cronParts[4];\n\n    $nextExecution = clone new DateTime('@' . $timestamp);\n    $nextExecution->modify('+1 minute');\n\n    while (true) {\n        if ($minute !== '*' && $nextExecution->format('i') !== $minute) {\n            $nextExecution->modify('+' . (60 - $nextExecution->format('i')) . ' minutes');\n            continue;\n        }\n\n        if ($hour !== '*' && $nextExecution->format('G') !== $hour) {\n            $nextExecution->modify('+' . (24 - $nextExecution->format('G')) . ' hours');\n            continue;\n        }\n\n        if ($day !== '*' && $nextExecution->format('j') !== $day) {\n            $nextExecution->modify('+' . (30 - $nextExecution->format('j')) . ' days');\n            continue;\n        }\n\n        // Month and day of week calculations can be handled similarly\n\n        // If we reach this point, the next execution time has been found\n        return $nextExecution->format('U'); // Return as Unix timestamp\n    }\n}