$sql = "\n    SELECT \n        cg.cat_group_name, \n        c.title, \n        COUNT(n.news_id) as news_count\n    FROM \n        CategoryGroups cg\n    JOIN \n        Categories c ON cg.cat_group_id = c.cat_group_id\n    LEFT JOIN \n        News n ON c.category_id = n.category_id\n    GROUP BY \n        cg.cat_group_name, \n        c.title\n    ORDER BY \n        cg.cat_group_name, \n        c.title\n";\n\n$result = mysql_query($sql);\n\nwhile ($row = mysql_fetch_assoc($result)) {\n    echo "<h3>" . $row['cat_group_name'] . "</h3>";\n    echo $row['title'] . " (" . $row['news_count'] . ")<br/>";\n}