// upload-proxy.php\n$video = $_FILES['video'];\n$apiUrl = 'https://api.viddler.com/api/v2/videos/upload';\n$apiKey = 'YOUR_API_KEY';\n\n$ch = curl_init($apiUrl);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $video);\ncurl_setopt($ch, CURLOPT_HTTPHEADER, array('Authorization: ' . $apiKey));\n\n$response = curl_exec($ch);\ncurl_close($ch);\n\necho $response;