<?php\n$allowed_dirs = array('images', 'uploads'); // whitelist allowed directories\n$foo = filter_var($_POST["foo"], FILTER_SANITIZE_STRING);\nif (!in_array($foo, $allowed_dirs)) {\n    die('Invalid directory');\n}\n$foo = realpath($foo); // resolve to absolute path\n$files = scandir($foo);\nforeach ($files as $a) {\n    echo "<img src='" . htmlspecialchars($foo . '/' . $a) . "'>";\n}\n?>