$route = new Zend_Controller_Router_Route(\n    ':module/:controller/:action/*',\n    array('module' => 'default', 'controller' => 'index', 'action' => 'index'),\n    array('module' => '[a-zA-Z0-9_-]+', 'controller' => '[a-zA-Z0-9_-]+', 'action' => '[a-zA-Z0-9_-]+')\n);\n\n$router = Zend_Controller_Front::getInstance()->getRouter();\n$router->addRoute('default', $route);\n\n// Update the route to include the subfolder\n$route->setRoute('/subfolder/:module/:controller/:action/*');