function generateUniqueUrlVariable($recordId) {\n  // Generate a random identifier using a CSPRNG\n  $randomId = random_bytes(16);\n\n  // Hash the identifier using SHA-256\n  $hashedId = hash('sha256', $randomId);\n\n  // Encode the hashed value using Base62\n  $encodedId = base62_encode($hashedId);\n\n  return $encodedId;\n}\n\n// Example usage:\n$recordId = 123; // Replace with your actual record ID\n$urlVariable = generateUniqueUrlVariable($recordId);\necho $urlVariable; // Output: e.g., "195yq"