// Create a new file in application/libraries called Database_loader.php\n\nclass Database_loader {\n\n    private $CI;\n\n    public function __construct() {\n        $this->CI =& get_instance();\n    }\n\n    public function load_databases() {\n        $this->CI->db = $this->CI->load->database('default', TRUE);\n        $this->CI->db_stats = $this->CI->load->database('stats', TRUE);\n    }\n\n}