class User {\n    private $name;\n    private $email;\n\n    public function __construct($name, $email) {\n        $this->name = $name;\n        $this->email = $email;\n    }\n\n    public function getName() {\n        return $this->name;\n    }\n\n    public function getEmail() {\n        return $this->email;\n    }\n}\n\n$user = new User('John Doe', 'john@example.com');\necho $user->getName(); // Output: John Doe\necho $user->getEmail(); // Output: john@example.com