class StaticDecorator {\n    public static function __callStatic($method, $args) {\n        // Your logic before calling the actual method\n        echo "Before calling $method\n";\n\n        return call_user_func_array(['MyClass', $method], $args);\n    }\n}\n\nclass MyClass {\n    public static function myMethod($arg1, $arg2) {\n        echo "Inside myMethod\n";\n        // Your method logic here\n    }\n}\n\n$decorator = new StaticDecorator();\n$decorator::myMethod('arg1', 'arg2');