class MyClass {\n    public function __invoke($method, $args) {\n        // Your logic before calling the actual method\n        echo "Before calling $method\n";\n\n        if (method_exists($this, $method)) {\n            return call_user_func_array([$this, $method], $args);\n        } else {\n            // Method does not exist, handle the error\n        }\n    }\n\n    public function myMethod($arg1, $arg2) {\n        echo "Inside myMethod\n";\n        // Your method logic here\n    }\n}\n\n$obj = new MyClass();\n$obj->__invoke('myMethod', ['arg1', 'arg2']);