<?php\n$mysqli = new mysqli('your_host', 'your_user', 'your_password', 'your_database');\n\n$post_id = 123;  // replace with the specific post_id\n\n// Get the tags for the specific post\n$tags_query = "SELECT t.tag_id, t.tag FROM Post_tag_nn pt JOIN Tags t ON pt.tag_id = t.tag_id WHERE pt.post_id = ?";\n$tags_result = $mysqli->query($tags_query);\n$tags = array();\nwhile ($tag = $tags_result->fetch_assoc()) {\n  $tags[] = $tag;\n}\n\n// Generate all possible subsets of the tags\n$subsets_query = "WITH RECURSIVE tag_subsets AS (...) SELECT * FROM tag_subsets";\n$subsets_result = $mysqli->query($subsets_query);\n$subsets = array();\nwhile ($subset = $subsets_result->fetch_assoc()) {\n  $subsets[] = $subset;\n}\n\n// Get the posts for each subset of tags\n$posts_query = "WITH RECURSIVE tag_subsets AS (...) SELECT ts.level, p.post_id, p.title, p.text FROM tag_subsets ts JOIN Post_tag_nn pt ON ts.tag_id = pt.tag_id JOIN Posts p ON pt.post_id = p.post_id GROUP BY ts.level, p.post_id, p.title, p.text ORDER BY ts.level DESC";\n$posts_result = $mysqli->query($posts_query);\n$posts = array();\nwhile ($post = $posts_result->fetch_assoc()) {\n  $posts[] = $post;\n}\n\n// Print the results\nforeach ($posts as $post) {\n  echo "Level: {$post['level']}, Post ID: {$post['post_id']}, Title: {$post['title']}, Text: {$post['text']}\n";\n}