$facebook = new Facebook(array(\n  'appId'  => 'YOUR_APP_ID',\n  'secret' => 'YOUR_APP_SECRET',\n));\n\n$video_path = 'path/to/your/video.mp4';\n$video_title = 'Your Video Title';\n$video_description = 'Your Video Description';\n\n$video_data = array(\n  'title' => $video_title,\n  'description' => $video_description,\n  'source' => '@' . realpath($video_path),\n);\n\ntry {\n  $video_id = $facebook->api('/me/videos', 'POST', $video_data);\n  echo "Video uploaded successfully! Video ID: " . $video_id['id'];\n} catch (FacebookApiException $e) {\n  echo "Error uploading video: " . $e->getMessage();\n}