<?php\n\n// Array lookup\n$lookup = array("a" => 1, "b" => 2, "c" => 3);\n$key = "b";\n$start = microtime(true);\n$value = $lookup[$key];\n$end = microtime(true);\necho "Array lookup: " . ($end - $start) . " seconds\n";\n\n// If-else stack\n$key = "b";\n$start = microtime(true);\nif ($key == "a") {\n    $value = 1;\n} elseif ($key == "b") {\n    $value = 2;\n} elseif ($key == "c") {\n    $value = 3;\n}\n$end = microtime(true);\necho "If-else stack: " . ($end - $start) . " seconds\n";\n\n// Switch statement\n$key = "b";\n$start = microtime(true);\nswitch ($key) {\n    case "a":\n        $value = 1;\n        break;\n    case "b":\n        $value = 2;\n        break;\n    case "c":\n        $value = 3;\n        break;\n}\n$end = microtime(true);\necho "Switch statement: " . ($end - $start) . " seconds\n";\n\n?>