require_once('fpdf.php');\nrequire_once('fpdi.php');\n\n// Load the PDF file\n$pdf = new FPDI();\n$pdf->setSourceFile('path/to/your/ebook.pdf');\n\n// Iterate through all pages\n$pageCount = $pdf->setSourceFile('path/to/your/ebook.pdf');\nfor ($i = 1; $i <= $pageCount; $i++) {\n    $tplIdx = $pdf->importPage($i);\n    $pdf->addPage();\n    $pdf->useTemplate($tplIdx);\n\n    // Replace the placeholder string with the email address\n    $pdf->setFont('Helvetica');\n    $pdf->setColor('black');\n    $pdf->setTextColor(0, 0, 0);\n    $pdf->setFontSize(12);\n    $pdf->setXY(10, 10); // Position where you want to insert the email address\n    $pdf->write(0, str_replace('EMAIL_TOKEN', 'user@example.com', $pdf->getPageText()));\n}\n\n// Output the updated PDF file\n$pdf->Output('path/to/your/updated/ebook.pdf', 'F');