public function login() {\n    if ($this->request->is('post')) {\n        // Check the referer URL\n        $referer = $this->referer();\n        if ($referer == '/') {\n            // If the form was posted from the home page, set the redirect URL\n            $this->Auth->loginRedirect = array('controller' => 'your_controller', 'action' => 'your_action');\n        } else {\n            // Use the default loginRedirect\n            $this->Auth->loginRedirect = $this->Auth->loginRedirect;\n        }\n    }\n    // The rest of your login logic...\n}