// default.php\n<ul id="handles" class="tabs">\n    <?php for ($i = 0, $n = count($list); $i < $n; $i ++) :\n            modSankeSlideHelper::getTabs($list[$i], $i == 0);\n     endfor; ?>                \n    <li class="end"></li>\n</ul>\n\n// helper.php\nfunction getTabs(&$item, $isFirst = false)\n{\n    global $mainframe;\n\n    $item->created     = $item->created;\n\n    list($year, $month, $day) = split("-", $item->created);\n    $tabdate = date('d\/m\/y', mktime(0, 0, 0, $month, $day, $year));\n\n    require(JModuleHelper::getLayoutPath('mod_sankeslide', '_tab'));\n}\n\n// _tab.php\n<li>\n    <?php if ($isFirst) : ?>\n        <a href="#tab" class="first-item">\n    <?php else : ?>\n        <a href="#tab">\n    <?php endif; ?>\n        <span><?php echo 'Shout ' . $tabdate; ?></span>\n        <b></b>\n    </a>\n</li>