$blahOptions = array(\n    'option_1' => true,\n);\n\nfunction BlahArray(array $options = array()) {\n    $defaults = array(\n        'option_1' => false,\n        'option_2' => 'blah',\n    );\n\n    foreach ($defaults as $defaultOption => $defaultValue) {\n        if (!isset($options[$defaultOption])) {\n            $options[$defaultOption] = $defaultValue;\n        } else {\n            // Validate and sanitize each option\n            switch ($defaultOption) {\n                case 'option_1':\n                    $options[$defaultOption] = (bool) $options[$defaultOption];\n                    break;\n                case 'option_2':\n                    $options[$defaultOption] = (string) $options[$defaultOption];\n                    break;\n            }\n        }\n    }\n\n    return $options;\n}