// BaseController.php\nclass BaseController {\n    // common functionality here\n}\n\n// ArticleController.php\nclass ArticleController extends BaseController {\n    // article-specific functionality here\n}\n\n// UserController.php\nclass UserController extends BaseController {\n    // user-specific functionality here\n}\n\n// SuperController.php\nclass SuperController {\n    private $controllers = array();\n\n    public function __construct() {\n        $this->controllers[] = new ArticleController();\n        $this->controllers[] = new UserController();\n    }\n\n    public function handleRequest() {\n        // determine which controller to use based on request parameters\n        foreach ($this->controllers as $controller) {\n            if ($controller->canHandleRequest()) {\n                $controller->handleRequest();\n                break;\n            }\n        }\n    }\n}