// partner-website.php\n\n$searchQuery = 'abc';\n$apiKey = 'their_api_key'; // provided by you\n\n$ch = curl_init('https://yourwebsite.com/api/search?q=' . urlencode($searchQuery));\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_HTTPHEADER, array('Authorization: ' . $apiKey));\n\n$response = curl_exec($ch);\ncurl_close($ch);\n\n// Process the search results (in JSON format)\n$results = json_decode($response, true);\nforeach ($results as $result) {\n    echo $result['word'] . '<br>';\n}