use PhpOffice\PhpPowerpoint\Reader\PowerPoint2007;\n\n$reader = new PowerPoint2007();\n$ppt = $reader->load('path/to/presentation.pptx');\n\nforeach ($ppt->getSlides() as $slide) {\n    $text = '';\n    foreach ($slide->getShapes() as $shape) {\n        if ($shape instanceof \PhpOffice\PhpPowerpoint\Shape\RichText) {\n            $text .= $shape->getText();\n        }\n    }\n    echo $text . "\n";\n}