<?php\n$interface = 'eth0'; // Change to your interface (e.g. eth0, wlan0)\n$rx_file = '/sys/class/net/' . $interface . '/statistics/rx_bytes';\n$tx_file = '/sys/class/net/' . $interface . '/statistics/tx_bytes';\n\n$previous_rx = file_get_contents($rx_file);\n$previous_tx = file_get_contents($tx_file);\n\nsleep(1); // Wait 1 second\n\n$current_rx = file_get_contents($rx_file);\n$current_tx = file_get_contents($tx_file);\n\n$download_speed = ($current_rx - $previous_rx) / 1024; // KB/s\n$upload_speed = ($current_tx - $previous_tx) / 1024; // KB/s\n\necho "Download Speed: $download_speed KB/s\n";\necho "Upload Speed: $upload_speed KB/s\n";