<?php\n// Connect to database\n$conn = mysqli_connect("localhost", "username", "password", "database");\n\n// Check connection\nif (!$conn) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n// Update prices query\n$query = "UPDATE items SET price = (SELECT new_price FROM market_data WHERE item_id = items.id)";\n\n// Execute query\nmysqli_query($conn, $query);\n\n// Close connection\nmysqli_close($conn);\n?>